import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import { ReviewOrderItem } from '..';
import { HelperService } from '/services';
import { DishOrderTotal } from '../../../Menu/components';

import './ReviewOrderTab.scss';

export class ReviewOrderTab extends PureComponent {
    mapOrderItems() {
        const { order } = this.props;

        return order.map((dish, index) => {
            return (
                <ReviewOrderItem
                    key={index}
                    {...{ dish }}
                    {...HelperService.pick(this.props, [
                        'removeDish',
                        'setDishExtra',
                        'removeDishExtra',
                        'setDishSize'
                    ])}
                />
            );
        });
    }

    render() {
        const { restaurant_url, handleNextStep, totalPrice, canNextStep } = this.props;

        return (
            <div className={'review-order-tab'}>
                <div className='review-order-tab__content'>
                    <div className='review-order-tab__items'>
                        {this.mapOrderItems()}
                    </div>

                    <DishOrderTotal
                        showCheckoutBtn={false}
                        className={'review-order-tab__total'}
                        {...HelperService.pick(totalPrice, [
                            'subtotal',
                            'deliveryFee',
                            'salesTax',
                            'total'
                        ])}
                    />

                    <div className='review-order-tab__step-controls'>
                        <Link to={`/r/${restaurant_url}`} className={'app-invisible-link'}>
                            <Button
                                variant={'contained'}
                                color={'secondary'}
                                className={'review-order-tab__back-menu'}
                            >
                                Back to Menu
                            </Button>
                        </Link>
                        <Button
                            variant={'contained'}
                            color={'primary'}
                            onClick={handleNextStep}
                            disabled={ !canNextStep }
                            className={'review-order-tab__process-payment'}
                        >
                            Process Payment
                        </Button>
                    </div>
                </div>
            </div>
        );
    }
}

ReviewOrderTab.propTypes = {
    handleNextStep: PropTypes.func,
    removeDish: PropTypes.func,
    setDishExtra: PropTypes.func,
    removeDishExtra: PropTypes.func,
    setDishSize: PropTypes.func,
    restaurantLink: PropTypes.string,
    order: PropTypes.array,
    totalPrice: PropTypes.object,
    canNextStep: PropTypes.bool

};

ReviewOrderTab.defaultProps = {
    canNextStep: false
}
