import React, { Component } from 'react';
import PropTypes from 'prop-types';
import IconButton from '@material-ui/core/IconButton';
import ClearIcon from '@material-ui/icons/Clear';

import './DishExtraRow.scss';

export class DishExtraRow extends Component {
    onRemoveDishExtraCLick = () => {
        const { removeDishExtra, extra: {id}, dishUUID } = this.props;
        removeDishExtra({ dishUUID, extraId: id });
    }

    render() {
        const {
            extra: { name, price }
        } = this.props;

        return (
            <div className={'dish-extra-row'}>
                <h4 className='dish-extra-raw__name'>
                    {name}  ${price}
                </h4>
                <IconButton
                    className={'dish-extra-raw__delete'}
                    color={'primary'}
                    onClick={this.onRemoveDishExtraCLick}
                    aria-label='Delete'
                >
                    <ClearIcon/>
                </IconButton>
            <hr/>
            </div>
        );
    }
}

DishExtraRow.propTypes = {
    removeDishExtra: PropTypes.func,
    dishUUID: PropTypes.string,
    extra: PropTypes.object,
};
