import {
    CREATE_DISH_REQUEST,
    CREATE_DISH_SUCCESS,
    CREATE_DISH_ERROR,
    UPDATE_DISH_REQUEST,
    UPDATE_DISH_SUCCESS,
    UPDATE_DISH_ERROR,
    GET_DISH_BY_ID_REQUEST,
    GET_DISH_BY_ID_ERROR,
    GET_DISH_BY_ID_SUCCESS
} from '/scenes/Menu/modules/menu.dishes.modules';

export const TOGGLE_DISH_MODAL = 'dish-modal/toggle';
export const CLEAR_MODAL_DATA = 'dish-modal/clear-data';
export const SET_DISH_MODAL_MODE = 'dish-modal/set-mode';
export const SET_SAME_EXTRA = 'dish-modal/set-same-extra';
export const SET_SAME_SIZE = 'dish-modal/set-same-size';
export const SET_MODAL_ADD_INITIAL_STATE = 'dish-modal/set-add-state';

export const getInitialState = () => ({
    loading: false,
    mode: 'add',
    initialValues: {
        sizes: [],
        extras: [],
    },
    error: null,
    open: false,
    id: null
});

export function reducer(state = getInitialState(), action) {
    switch (action.type) {
        case UPDATE_DISH_REQUEST:
        case GET_DISH_BY_ID_REQUEST:
        case CREATE_DISH_REQUEST: {
            return {
                ...state,
                loading: true
            }
        }

        case CLEAR_MODAL_DATA: {
            return getInitialState();
        }

        case GET_DISH_BY_ID_SUCCESS: {
            return {
                ...state,
                initialValues: action.response,
                loading: false
            }
        }

        case UPDATE_DISH_SUCCESS:
        case CREATE_DISH_SUCCESS:
        case CREATE_DISH_ERROR: {
            return {
                ...state,
                loading: false
            }
        }

        case SET_MODAL_ADD_INITIAL_STATE: {
            return {
                ...getInitialState(),
                open: true
            }
        }

        case SET_DISH_MODAL_MODE: {
            return {
                ...state,
                mode: action.mode
            }
        }

        case GET_DISH_BY_ID_ERROR:
        case UPDATE_DISH_ERROR: {
            return {
                ...state,
                loading: false,
                error: action.error
            }
        }

        case TOGGLE_DISH_MODAL: {
            const {id, open} = action;
            return {
                ...state,
                open,
                id
            }
        }

        default: {
            return state
        }
    }
}

export function dishReducerPlugin(state, action) {
    switch(action.type) {
        case SET_SAME_EXTRA:
            const { extra } = action.dish;
            return {
                ...state,
                values: {
                    ...state.values,
                    extra,
                }
            };
        case SET_SAME_SIZE:
            const { sizes } = action.dish;
            return {
                ...state,
                values: {
                    ...state.values,
                    sizes,
                }
            };
        default:
            return state
    }
}

export function toggleDishModal({id, open} = {open: true}) {
    return {
        type: TOGGLE_DISH_MODAL,
        id,
        open
    }
}

export function setMode(mode = 'add') {
    return {
        type: SET_DISH_MODAL_MODE,
        mode
    }
}

export function setSameExtra(dish) {
    return {
        type: SET_SAME_EXTRA,
        dish
    }
}

export function setSameSize(dish) {
    return {
        type: SET_SAME_SIZE,
        dish
    }
}

export function clearModalData() {
    return {
        type: CLEAR_MODAL_DATA
    }
}


export function setAddState() {
    return {
        type: SET_MODAL_ADD_INITIAL_STATE
    }
}
