import React, { PureComponent } from 'react';
import { connect } from 'react-redux';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import PropTypes from 'prop-types';
import { HelperService } from '/services';
import { Preloader } from '/components';

import {
    OrderPlacedTab,
    PaymentDetailsTab,
    ReviewOrderTab
} from './components';


import {
    orderForCheckoutSelector,
    totalPriceSelector,
    setDishExtra,
    removeDishExtra,
    setDishSize,
    cleanOrder,
    removeDish,
    restaurantLinkSelector,
    onRestaurantLoaded,
    restaurantLoadingSelector,
    orderForPaymentSelector
} from '../Menu/modules/';

import { getRestaurantById, managedRestaurantSelector } from '/scenes/Restaurants/modules';

import {
    loadingCreateOrderSelector,
    createOrder,
    abortPageRequests
} from '../Order/modules';

const mapDispatchToProps = {
    setDishExtra,
    removeDishExtra,
    setDishSize,
    removeDish,
    createOrder,
    cleanOrder,
    abortPageRequests,
    onRestaurantLoaded,
    getRestaurantById,
};

const mapStateToProps = state => ({
    order: orderForCheckoutSelector(state),
    totalPrice: totalPriceSelector(state),
    restaurantLink: restaurantLinkSelector(state),
    loadingCreateOrder: loadingCreateOrderSelector(state),
    restaurantLoading: restaurantLoadingSelector(state),
    restaurant: managedRestaurantSelector(state),
    orderForPayment: orderForPaymentSelector(state),
});

export default
@connect(
    mapStateToProps,
    mapDispatchToProps
)
class Checkout extends PureComponent {
    state = {
        activeStep: 0
    };

    componentDidMount() {
        const {
            getRestaurantById,
            onRestaurantLoaded,
            history: {
                location: { pathname }
            }
        } = this.props;

        const restaurant_url = pathname.split('/')[2];

        const restaurantPayload = {
            id: restaurant_url,
            body: { isClient: true }
        };
        getRestaurantById(restaurantPayload).promise.then(onRestaurantLoaded);
    }

    handleNextStep = () => {
        this.setState(state => ({
            activeStep: state.activeStep + 1
        }));
    };

    handleBackStep = () => {
        this.setState(state => ({
            activeStep: state.activeStep - 1
        }));
    };

    handleResetStep = () => {
        this.setState({
            activeStep: 0
        });
    };

    getSteps() {
        return ['Review your order', 'Payment details', 'Order placed'];
    }

    getStepContent(step) {
        const {
            history: {
                location: { pathname }
            },
            totalPrice: {total},
            orderForPayment,
        } = this.props;

        const restaurant_url = pathname.split('/')[2];

        switch (step) {
            case 0: {
                return (
                    <ReviewOrderTab
                        {...HelperService.pick(this.props, [
                            'restaurantLink',
                            'removeDishExtra',
                            'setDishExtra',
                            'setDishSize',
                            'totalPrice',
                            'removeDish',
                            'order'
                        ])}
                        canNextStep={ !!orderForPayment.length }
                        restaurant_url={restaurant_url}
                        handleNextStep={this.handleNextStep}
                    />
                );
            }
            case 1: {
                return (
                    <PaymentDetailsTab
                        {...HelperService.pick(this.props, [
                            'cleanOrder',
                            'createOrder',
                            'orderForPayment',
                            'abortPageRequests',
                            'loadingCreateOrder',
                            'restaurant'
                        ])}
                        handleNextStep={ this.handleNextStep }
                        orderPriceInCoins={ HelperService.toCoins(total) } 
                    />
                );
            }
            case 2: {
                return (
                    <OrderPlacedTab
                        {...HelperService.pick(this.props, ['restaurantLink'])}
                    />
                );
            }
            default: {
                return null;
            }
        }
    }

    mapSteps() {
        const steps = this.getSteps();

        return steps.map(label => {
            return (
                <Step key={label}>
                    <StepLabel>{label}</StepLabel>
                </Step>
            );
        });
    }

    render() {
        const { activeStep } = this.state;
        const { restaurantLoading } = this.props;

        return (
            <div className={'checkout-container app-static-container'}>
                <Stepper
                    alternativeLabel
                    nonLinear
                    activeStep={activeStep}
                    className={'order-stepper'}
                >
                    {this.mapSteps()}
                </Stepper>
                {restaurantLoading ? (
                    <Preloader />
                ) : (
                    this.getStepContent(activeStep)
                )}
            </div>
        );
    }
}

Checkout.propTypes = {
    orderForPayment: PropTypes.array,
    order: PropTypes.array,
    setDishExtra: PropTypes.func,
    removeDishExtra: PropTypes.func,
    setDishSize: PropTypes.func,
    removeDish: PropTypes.func,
    restaurant: PropTypes.string,
    restaurantLink: PropTypes.string,
    totalPrice: PropTypes.object,
    orderLoading: PropTypes.bool,
    createOrder: PropTypes.func
};
