import React, { Component } from 'react';
import Phone from '@material-ui/icons/Phone';
import Email from '@material-ui/icons/Email';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Typography from '@material-ui/core/Typography';
import PropTypes from 'prop-types';
import { HelperService } from '/services';
import moment from 'moment';

import './RestaurantInfo.scss';
export class RestaurantInfo extends Component {
    mapHourRows(section) {
        return Object.keys(section).map((day, index) => {
            if (section[day].isClosed) {
                return (
                    <Typography className={'info__hours-row'} key={index}>
                        <strong>{day}</strong>
                        <strong>Closed</strong>
                    </Typography>
                );
            }

            return (
                <Typography className={'info__hours-row'} key={index}>
                    <strong className={'info__hours-row-day'}>{day}</strong>
                    <span>{moment(section[day].start).format('hh:mm A')}</span>
                    {'-'}
                    <span>{moment(section[day].end).format('hh:mm A')}</span>
                </Typography>
            );
        });
    }

    mapHourSections() {
        const {
            restaurant: { hours }
        } = this.props;

        if (!hours) {
            return null;
        }

        return Object.keys(hours).map(section => {
            return (
                <ExpansionPanel className={'info__hours-panel'}>
                    <ExpansionPanelSummary
                        expandIcon={<ExpandMoreIcon />}
                        color={'primary'}
                    >
                        <Typography>
                            {HelperService.capitalize(section) + ' hours'}
                        </Typography>
                    </ExpansionPanelSummary>
                    <ExpansionPanelDetails className={'info__hours-details'}>
                        {this.mapHourRows(hours[section])}
                    </ExpansionPanelDetails>
                </ExpansionPanel>
            );
        });
    }

    render() {
        const {
            restaurant: {
                restaurant_name,
                street_address,
                state,
                zipcode,
                phone_number,
                delivery_email,
                tags
            }
        } = this.props;

        return (
            <div className={'restaurant-info'}>
                <h1>{restaurant_name}</h1>
                <div className='info__description'>
                    <div className='info__description-item'>
                        {street_address}, {state}, {zipcode}
                    </div>
                    <div className='info__description-item'>
                        <div className={'info__description-item-contact'}>
                            <Phone color={'primary'} />
                            <span>{phone_number}</span>
                        </div>
                        <div className={'info__description-item-contact'}>
                            <Email color={'primary'} />
                            <span>{delivery_email}</span>
                        </div>
                    </div>
                    <div className='info__description-item'>
                        <span>{tags}</span>
                    </div>
                </div>
                <div className='info__hour-sections'>
                    {this.mapHourSections()}
                </div>
            </div>
        );
    }
}

RestaurantInfo.propTypes = {
    restaurant: PropTypes.object
};
