import {
    CREATE_CATEGORY_REQUEST,
    CREATE_CATEGORY_SUCCESS,
    CREATE_CATEGORY_ERROR,
    UPDATE_CATEGORY_REQUEST,
    UPDATE_CATEGORY_SUCCESS,
    UPDATE_CATEGORY_ERROR,
    GET_CATEGORY_BY_ID_REQUEST,
    GET_CATEGORY_BY_ID_ERROR,
    GET_CATEGORY_BY_ID_SUCCESS
} from '/scenes/Menu/modules/menu.categories.modules'

export const TOGGLE_CATEGORY_MODAL = 'category-modal/toggle';
export const CLEAR_MODAL_DATA = 'category-modal/clear-data';
export const SET_CATEGORY_MODAL_MODE = 'category-modal/set-mode';

export const getInitialState = () => ({
    loading: false,
    mode: 'add',
    initialValues: {},
    error: null,
    open: false,
    id: null
});

export function reducer(state = getInitialState(), action) {
    switch (action.type) {
        case UPDATE_CATEGORY_REQUEST:
        case CREATE_CATEGORY_REQUEST:
        case GET_CATEGORY_BY_ID_REQUEST: {
            return {
                ...state,
                loading: true
            }
        }

        case CLEAR_MODAL_DATA: {
            return {
                ...state,
                initialValues: {}
            }
        }

        case GET_CATEGORY_BY_ID_SUCCESS: {
            return {
                ...state,
                initialValues: action.response,
                loading: false
            }
        }

        case UPDATE_CATEGORY_SUCCESS:
        case CREATE_CATEGORY_SUCCESS: {
            return {
                ...state,
                loading: false
            }
        }

        case SET_CATEGORY_MODAL_MODE: {
            return {
                ...state,
                mode: action.mode
            }
        }

        case GET_CATEGORY_BY_ID_ERROR:
        case UPDATE_CATEGORY_ERROR:
        case CREATE_CATEGORY_ERROR: {
            return {
                ...state,
                loading: false,
                error: action.error
            }
        }

        case TOGGLE_CATEGORY_MODAL: {
            const {id, open} = action;
            return {
                ...state,
                open,
                id
            }
        }

        default: {
            return state
        }
    }
}

export function toggleCategoryModal({id, open} = {open: true}) {
    return {
        type: TOGGLE_CATEGORY_MODAL,
        id,
        open
    }
}

export function setMode(mode = 'add') {
    return {
        type: SET_CATEGORY_MODAL_MODE,
        mode
    }
}

export function clearModalData() {
    return {
        type: CLEAR_MODAL_DATA
    }
}
