import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import IconButton from '@material-ui/core/IconButton';
import Clear from '@material-ui/icons/Clear';

import './BookedItem.scss';

export class BookedItem extends PureComponent {
    onRemoveClick = event => {
        const {
            dish: { id },
            removeDishFromOrder
        } = this.props;
        event.preventDefault();

        removeDishFromOrder(id);
    };

    render() {
        const {
            dish: { name, finalPrice }
        } = this.props;

        return (
            <div className={'booked-item'}>
                <div className='booked-item__count'>
                    <IconButton
                        color='primary'
                        aria-label='Add'
                        onClick={this.onRemoveClick}
                    >
                        <Clear />
                    </IconButton>
                </div>
                <div className='booked-item__description'>
                    <div className='booked-item__description-name'>{name}</div>
                    <div className='booked-item__description-price'>
                        $ {finalPrice}
                    </div>
                </div>
            </div>
        );
    }
}

BookedItem.propTypes = {
    dish: PropTypes.object,
    removeDishFromOrder: PropTypes.func
};
