import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import { autobind } from 'core-decorators';
import Dropzone from 'react-dropzone';
import ReactCrop from 'react-image-crop';
import './AppDropzone.scss';

import 'react-image-crop/lib/ReactCrop.scss';

export class AppDropzone extends PureComponent {

    @autobind
    onDrop(accepted, rejected) {
        const {onDrop} = this.props;
        onDrop(accepted && accepted[0]);
    }

    renderPreview(src) {
        const {crop, onChange, useCrop, file} = this.props;

        if (!useCrop || !(file instanceof File)) {
            return (
                <img src={ src } alt="" className={ 'app-dropzone__image' }/>
            );
        }

        return (
            <ReactCrop
                { ...{
                    src,
                    crop,
                    onChange
                } }
            />
        );
    }

    render() {
        const {id, file, accept, label} = this.props;
        const src = file instanceof File ? URL.createObjectURL(file) : file;

        return (
            <div className={ 'app-dropzone__wrapper' }>
                { src && this.renderPreview(src) }
                <Dropzone
                    { ...{
                        id,
                        accept
                    } }
                    className="app-dropzone__area"
                    onDrop={ this.onDrop }>
                    <div className="app-dropzone__content">
                        <div className="app-dropzone__description">
                            <h3>{ label } </h3>
                            <h5>Drag and drop picture to add it</h5>
                            <div id="chosen-files">
                                { file && <div>{ file.name }</div> }
                            </div>
                        </div>
                    </div>
                </Dropzone>
            </div>
        )
    }
}

AppDropzone.propTypes = {
    id: PropTypes.string,
    accept: PropTypes.array.isRequired,
    onDrop: PropTypes.func.isRequired,
    useCrop: PropTypes.bool,
    onChange: PropTypes.func,
    crop: PropTypes.shape({
        x: PropTypes.number,
        y: PropTypes.number,
        width: PropTypes.number,
        height: PropTypes.number
    }),
};
