import { resource } from './dish-autocomplete.modules';
import { createSelector } from 'reselect';

export const _dishAutocompleteSliceSelector = state => state[resource];
export const dishesListSelector = createSelector([_dishAutocompleteSliceSelector], slice => {
    return slice.data.map(({name, id, extra, sizes}) => ({label: name, value: id, extra, sizes}));
});

export const dishesFiltersSelector = state => _dishAutocompleteSliceSelector(state).filters;
export const dishesLoadingSelector = state => _dishAutocompleteSliceSelector(state).loading;
export const dishesCountSelector = state => _dishAutocompleteSliceSelector(state).count;
