import React from 'react';
import PropTypes from 'prop-types';
import { Field } from 'redux-form';

import { ZipCodeAutoComplete } from '/scenes/ZipCodeAutoComplete';
import { AppFormTextField } from '/components';

// field-level redux-form validator
const required = value => value ? undefined : 'Required'

const AddressFormFields = ({ zipcode, onChangeZipCode, }) => (
  <>
      <ZipCodeAutoComplete
          value={zipcode}
          compareKey={'value'}
          onChange={onChangeZipCode}
      />

      <Field
          required
          multiline
          type='text'
          name='city'
          label='City'
          validate={required}
          component={AppFormTextField}
      />

      <Field
          required
          type='text'
          name='street_address'
          label='Street address'
          validate={required}
          component={AppFormTextField}
      />

      <Field
          required
          multiline
          type='text'
          name='building'
          label='Building'
          validate={required}
          component={AppFormTextField}
      />

      <Field
          required
          multiline
          type='text'
          name='appartment'
          label='Appartment'
          validate={required}
          component={AppFormTextField}
      />
  </>
);

AddressFormFields.propTypes = {
    zipcode: PropTypes.string,
    onChangeZipCode: PropTypes.func.isRequired,
}

export default AddressFormFields;
