import React, { PureComponent } from 'react';
import Drawer from '@material-ui/core/Drawer';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

import {
    orderForCheckoutSelector,
    removeDishFromOrder,
    totalPriceSelector
} from '../../../Menu/modules';
import { DishOrder } from '../../../Menu/components';
import Hidden from '@material-ui/core/Hidden';

import './ClientNav.scss';

const mapStateToProps = (state, ownProps) => ({
    order: orderForCheckoutSelector(state),
    totalPrice: totalPriceSelector(state)
});

const mapDispatchToProps = {
    removeDishFromOrder
};

export default
@connect(
    mapStateToProps,
    mapDispatchToProps
)
class ClientNav extends PureComponent {
    state = {
        openedDrawer: false
    };

    toggleDrawer = () => {
        this.setState({ openedDrawer: !this.state.openedDrawer });
    };

    render() {
        const { totalPrice, removeDishFromOrder, order } = this.props;

        return (
            <div className={'header-nav'}>
                <Hidden mdUp>
                    <p
                        onClick={this.toggleDrawer}
                        className={'header-nav__price'}
                    >
                        $ {totalPrice.total}
                    </p>
                    <Drawer
                        anchor='right'
                        onClose={this.toggleDrawer}
                        open={this.state.openedDrawer}
                    >
                        <div
                            tabIndex={0}
                            role='button'
                            className={'header-nav__order-drawer'}
                        >
                            <div>
                                <DishOrder
                                    {...{
                                        totalPrice,
                                        removeDishFromOrder,
                                        order
                                    }}
                                />
                            </div>
                        </div>
                    </Drawer>
                </Hidden>
            </div>
        );
    }
}

ClientNav.propTypes = {
    price: PropTypes.number,
    order: PropTypes.array,
    removeDishFromOrder: PropTypes.func
};
