import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Field } from 'redux-form';
import { autobind } from 'core-decorators';
import IconButton from '@material-ui/core/IconButton';
import Delete from '@material-ui/icons/Delete';

import { AppFormTextField } from '/components';

export class DishModalSizeItem extends Component {
    @autobind
    onRemoveClick() {
        const { fields, index } = this.props;

        fields.remove(index);
    }

    render() {
        const { sizeItem } = this.props;

        return (
            <li className={'dish-modal__array-field-row'}>
                <Field
                    name={`${sizeItem}.name`}
                    type='text'
                    className={'dish-modal__array-field-input'}
                    component={AppFormTextField}
                    label='Dish size'
                />
                <Field
                    name={`${sizeItem}.price`}
                    type='text'
                    className={'dish-modal__array-field-input'}
                    component={AppFormTextField}
                    label='Dish price'
                />

                <IconButton onClick={this.onRemoveClick}>
                    <Delete />
                </IconButton>
            </li>
        );
    }
}

DishModalSizeItem.propTypes = {
    sizeItem: PropTypes.string,
    index: PropTypes.number
};
DishModalSizeItem.defaultProps = {};
