import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Field } from 'redux-form';
import { autobind } from 'core-decorators';
import IconButton from '@material-ui/core/IconButton';
import Delete from '@material-ui/icons/Delete';

import { AppFormTextField } from '/components';

export class DishModalExtraItem extends Component {

    @autobind
    onRemoveClick() {
        const {fields, index} = this.props;

        fields.remove(index);
    }

    render() {
        const {extra} = this.props;

        return (
            <li className={ 'dish-modal__array-field-row' }>
                <Field
                    name={ `${extra}.name` }
                    type="text"
                    className={ 'dish-modal__array-field-input' }
                    component={ AppFormTextField }
                    label="Extra name"
                />
                <Field
                    name={ `${extra}.price` }
                    type="text"
                    className={ 'dish-modal__array-field-input' }
                    component={ AppFormTextField }
                    label="Extra price"
                />

                <IconButton onClick={ this.onRemoveClick }>
                    <Delete/>
                </IconButton>
            </li>
        );
    }
}

DishModalExtraItem.propTypes = {
    extra: PropTypes.string
};
DishModalExtraItem.defaultProps = {};
