import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import { DishExtraRow } from '../DishExtraRow';
import { ExtrasSelect, SizesSelect } from '..';
import Paper from '@material-ui/core/Paper';

import './ReviewOrderItem.scss';

export class ReviewOrderItem extends PureComponent {
    mapDishExtras() {
        const { dish, removeDishExtra } = this.props;
        const { selectedExtras, uuid } = dish;

        return selectedExtras.map((extraId, index) => {
            const extra = dish.extra.find(extra => extra.id === extraId)
            return (
                <DishExtraRow
                    key={index}
                    dishUUID={uuid}
                    {...{
                        extra,
                        removeDishExtra
                    }}
                />
            );
        });
    }

    render() {
        const {
            dish: {
                name,
                description,
                picture,
                finalPrice,
                selectedExtras,
                extra = [],
                sizes = [],
                uuid,
                selectedSize
            },
            setDishExtra,
            setDishSize
        } = this.props;

        const size = sizes.find(size => size.id === selectedSize);

        return (
            <Paper className={'review-order-item'}>
                {picture && (
                    <img src={picture} className='review-order-item__image' />
                )}

                <div className='review-order-item__info'>
                    <h3 className='review-order-item__name'>{name}</h3>
                    <h3 className='review-order-item__price'>
                        {' '}
                        $ {finalPrice}
                    </h3>
                    <div className='review-order-item__description'>
                        {description}
                    </div>
                    {size && (
                        <h4 className='review-order-item__size'>
                            {size.name} size
                        </h4>
                    )}
                    <div className='review-order-item__extras'>
                        {this.mapDishExtras()}
                    </div>
                </div>
                <div className='review-order-item__controls'>
                    {extra && (
                        <ExtrasSelect
                            dishUUID={uuid}
                            {...{ setDishExtra }}
                            excludedIds={selectedExtras}
                            options={extra}
                        />
                    )}

                    {sizes && (
                        <SizesSelect
                            dishUUID={uuid}
                            {...{ setDishSize }}
                            options={sizes}
                            value={selectedSize}
                        />
                    )}
                </div>
            </Paper>
        );
    }
}

ReviewOrderItem.propTypes = {
    removeDish: PropTypes.func,
    setDishExtra: PropTypes.func,
    removeDishExtra: PropTypes.func,
    setDishSize: PropTypes.func,
    dish: PropTypes.object
};
